import React, { useEffect, useState, useContext } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import CardGrid from '../card/CardGrid';
import Toast from './Toast';
import TeamMembersContext from '../../store/teamMembers-context';

const Results = () => {
  const [herosList, setHerosList] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [heroAdded, setHeroAdded] = useState(false);

  const { heros } = useContext(TeamMembersContext);

  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const heroName = queryParams.get('heroName');

  const endpoint = `http://localhost:5000/getHeros/?heroName=${heroName}`;

  useEffect(() => {
    setIsLoading(true);
    const fetchHeroes = async () => {
      const response = await axios.get(endpoint);
      setIsLoading(false);
      response.data.response === 'success'
        ? setHerosList(response.data.results)
        : console.log('error');
    };

    fetchHeroes();
  }, [endpoint]);

  useEffect(() => {
    const timeId = setTimeout(() => {
      if (heros.length !== 0) setHeroAdded(true);
    }, 1000);

    return () => {
      clearTimeout(timeId);
    };
  }, [heros]);

  return (
    <section>
      <div className='container mt-5 pb-5'>
        {isLoading && (
          <div className='d-flex justify-content-center'>
            <div className='spinner-border text-primary' role='status'></div>
          </div>
        )}
        {Boolean(herosList.length > 0) && !isLoading && (
          <CardGrid herosList={herosList} inHome={false} />
        )}
      </div>
      {heroAdded && <Toast />}
    </section>
  );
};

export default Results;
