import React from 'react';

const Card = ({ hero }) => {
  const { image, name } = hero;

  return (
    <div className='card bg-dark text-white mt-2'>
      <img
        style={{ height: '500px' }}
        src={image.url}
        className='card-img'
        alt={`${name} card`}
      />
      <div className='card-img-overlay d-flex flex-column justify-content-between'>
        <div
          className='p-3 mb-2'
          style={{
            backgroundColor: 'rgba(48, 44, 44, 0.9)',
            width: '300px',
            height: '75px',
          }}
        >
          <h3 className='card-title text-center'>{name}</h3>
        </div>

        <div className='d-flex justify-content-around'>
          <button type='button' className='btn btn-success mr-3'>
            Add Hero
          </button>
          <button type='button' className='btn btn-primary'>
            Details
          </button>
        </div>
      </div>
    </div>
  );
};

export default Card;
