export const isHeroInTeam = (enteredHero, team) => {
  return team.includes(enteredHero);
};

export const teamSummary = (team) => {
  const powerstatsSummary = {
    intelligence: 0,
    strength: 0,
    speed: 0,
    durability: 0,
    power: 0,
    combat: 0,
  };

  team.forEach((hero) => {
    powerstatsSummary.intelligence += parseInt(hero.porwestats.intelligence);
    powerstatsSummary.strength += parseInt(hero.porwestats.strength);
    powerstatsSummary.speed += parseInt(hero.porwestats.speed);
    powerstatsSummary.durability += parseInt(hero.porwestats.durability);
    powerstatsSummary.power += parseInt(hero.porwestats.power);
    powerstatsSummary.combat += parseInt(hero.porwestats.combat);
  });

  return powerstatsSummary;
};
