export const isHeroInTeam = (enteredHero, team) => {
  return team.includes(enteredHero);
};

export const checkTeamOrientation = (goodHerosCounter, badHerosCounter) => {
  if (goodHerosCounter > 3 || badHerosCounter > 3) {
    return false;
  }
  return true;
};
