export const isHeroInTeam = (enteredHero, team) => {
  return team.includes(enteredHero);
};

export const checkGoodHerosInTeam = (goodHerosCounter) => {
  if (goodHerosCounter === 3) return true;
};

export const checkBadHerosInTeam = (badHerosCounter) => {
  if (badHerosCounter === 3) return true;
};
