import React, { useContext } from 'react';
import {
  ResponsiveContainer,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
} from 'recharts';
import TeamMembersContext from '../../store/teamMembers-context';

const TeamStats = () => {
  const { teamSummary } = useContext(TeamMembersContext);

  console.log(teamSummary);

  let content;

  if (!teamSummary) {
    content = 0;
  } else {
    content = teamSummary;
  }

  return (
    <div style={{ backgroundColor: '#1a2950' }} className='container rounded'>
      <div className='row pt-5 pb-5'>
        <div className='col'>
          <ul style={{ color: '#abdbe3' }} className='list-unstyled'>
            {}
            <li className='pt-1'>Team Intelligence: {content} </li>
            <li className='pt-1'>Team Combat: </li>
            <li className='pt-1'>Team Strength: </li>
            <li className='pt-1'>Team Power: </li>
            <li className='pt-1'>Team Speed: </li>
            <li className='pt-1'>Team Durability: </li>
            <li className='pt-1'>Strongest Powerstat:</li>
            <li className='pt-1'>Average Weight:</li>
            <li className='pt-1'>Average Height:</li>
          </ul>
        </div>
        <div className='col'>
          <ResponsiveContainer width='100%' height='100%'>
            <RadarChart outerRadius={88} cx={140} cy={115} data={teamSummary}>
              <PolarGrid stroke='#abdbe3' />
              <PolarAngleAxis dataKey='powerstat' stroke='#abdbe3' />
              <PolarRadiusAxis angle={30} domain={[0, 150]} stroke='#abdbe3' />
              <Radar
                dataKey='value'
                stroke='#8884d8'
                fill='#8884d8'
                fillOpacity={0.6}
              />
            </RadarChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default TeamStats;
