export const isHeroInTeam = (enteredHero, team) => {
  return team.includes(enteredHero);
};

export const getTeamSummary = (team) => {
  const powerstatsSummary = {
    intelligence: 0,
    strength: 0,
    speed: 0,
    durability: 0,
    power: 0,
    combat: 0,
  };

  team.forEach((hero) => {
    console.log(hero.powerstats);
    powerstatsSummary.intelligence += 1;
    console.log(powerstatsSummary.intelligence);
  });

  return powerstatsSummary;
};
