export const isHeroInTeam = (enteredHero, team) => {
  return team.includes(enteredHero);
};

export const getTeamSummary = (team) => {
  const powerstatsSummary = {
    intelligence: 0,
    strength: 0,
    speed: 0,
    durability: 0,
    power: 0,
    combat: 0,
  };

  team.forEach((hero) => {
    powerstatsSummary.intelligence += parseInt(hero.powerstats.intelligence);
    powerstatsSummary.strength += parseInt(hero.porwerstats.strength);
    powerstatsSummary.speed += parseInt(hero.porwerstats.speed);
    powerstatsSummary.durability += parseInt(hero.porwerstats.durability);
    powerstatsSummary.power += parseInt(hero.porwerstats.power);
  });

  return powerstatsSummary;
};
